# # This is our blog controller
#
# We'll use this file to handle initially populating the database when a user visits `/bootstrap` for the first time.


Wafelijzer::App.controllers :bootstrap do

  # This is the route at `/bootstrap`
  get :index, :map => '/bootstrap' do
    # First check and see if the database has already been bootstrapped.
    # If it has, don't bootstrap it again.
    if (setting_value 'bootstrapped')
      @alert = "already bootstrapped :("
      @alert_type = "danger"

    # If it hasn't, let's go!
    else
      # First create an admin account
      if Account.create(:email => "admin@example.com", :password => 'password', :password_confirmation => 'password', :role => "admin")

        # It worked! Let's make sure nobody bootstraps this again.
        Setting.create(:title => "bootstrapped", :body => "true")

        # If the admin account worked, and we can successfully write to the database, continue creating stuff.

        # like an artist!
        leaftype = Artist.create(
          :name => "LEAFTYPE",
          :slug => "leaftype",
          :real_name => "Christopher Knollmeyer",
          :soundcloud => "leaftype",
          :bio => "Leaftype is Christopher Knollmeyer, an electronic musician from Lincoln, MA. He studies music technology at Calarts. He also plays keyboards and drums.",
          :image_url => "http://i.imgur.com/3XAhXnD.jpg"
        )

        # or an album!
        album = Album.create(
          :label => "Belgian Man Records",
          :type => "EP",
          :bandcamp_url => "http://leaftype.bandcamp.com/album/ltep0008"
        )
        # which we need to get info about
        album.populate_from_bandcamp
        # and assign to our artist
        AlbumsArtists.create(
          :artist_id => leaftype.id,
          :album_id => album.id,
          :role => "Producer"
        )

        # and a video!
        video = Video.create(
          :service => "vimeo",
          :vimeo_id => "34479631",
        )
        # which we need to get info about
        video.populate_from_vimeo
        # and assign to our artist
        ArtistsVideos.create(
          :artist_id => leaftype.id,
          :video_id => video.id,
          :role => "Performer"
        )

        # now let's make some press!
        press = Blog.create(
          :title => 'Example Blog Post about LEAFTYPE',
          :body => 'This is an example press article. Read it on P4K!',
          :type => 'press',
          :release_date => Date.today
        )
        # and assign it
        press.add_artist(leaftype)

        # Let's make an event, too!
        event = Blog.create(
          :title => 'Example LEAFTYPE event',
          :body => 'This is an example event. Check out our [facebook](http://facebook.com) page or something!',
          :type => 'events',
          :release_date => Date.today
        )
        # and assign it
        event.add_artist(leaftype)

        # We need to populate the website footer
        Text.create(
          :title => 'footer',
          :body => 'All music is licensed under a [Creative Commons by-nc](http://creativecommons.org/licenses/by-nc/3.0/) license.'
        )

        # and the description on the front page
        Text.create(
          :title => 'description',
          :body => "Trappist Records was founded in Belgium in 1989. We specialize in music about waffle-making."
        )

        # And set the label name
        Setting.create(
          :title => 'label_name',
          :body => "Trappist Records"
        )

        # and label logo
        Setting.create(
          :title => 'logo',
          :body => "http://i.imgur.com/TUvQfGd.png",
        )

        # maybe we should sell some merch
        Setting.create(
          :title => 'stripe_secret_key',
          :body => "sk_test_ry0vKP7GATV26tzMpqGraNPE",
        )
        Setting.create(
          :title => 'stripe_publishable_key',
          :body => "pk_test_lf3SMglj5Ng56vcgPMvVWmaR",
        )
        Merch.create(
          :title => 'purpshirt',
          :slug => "shirt",
          :type => "shirt",
          :release_date => Date.today,
          :image_url => 'http://i.imgur.com/0DnpEir.jpg',
          :price_in_cents => 20_00,
          :quantity => "10",
          :about => "This is a cool purple shirt! You should buy it!",
        )
        cd = Merch.create(
          :title => 'compact disc',
          :slug => "cd",
          :type => "cd",
          :release_date => Date.today,
          :image_url => 'http://i.imgur.com/sLD3b79.jpg',
          :price_in_cents => 10_00,
          :quantity => "100",
          :about => "This is a cool purple CD! You should buy it RIGHT NOW!",
        )
        leaftype.add_merch(cd)

        # finally here's a theme
        Theme.create(
          :title => 'liege',
          :css =>   '/*!' + "\n" +
                    '* Wafelijzer theme generated using the Liege Customizer (http://wafelijzer.belgianman.com)' + "\n" +
                    '*//*! normalize.css v3.0.1 | MIT License | git.io/normalize */html{font-family:sans-serif;-ms-text-size-adjust:100%;-webkit-text-size-adjust:100%}body{margin:0}article,aside,details,figcaption,figure,footer,header,hgroup,main,nav,section,summary{display:block}audio,canvas,progress,video{display:inline-block;vertical-align:baseline}audio:not([controls]){display:none;height:0}[hidden],template{display:none}a{background:transparent}a:active,a:hover{outline:0}abbr[title]{border-bottom:1px dotted}b,strong{font-weight:bold}dfn{font-style:italic}h1{font-size:2em;margin:0.67em 0}mark{background:#ff0;color:#000}small{font-size:80%}sub,sup{font-size:75%;line-height:0;position:relative;vertical-align:baseline}sup{top:-0.5em}sub{bottom:-0.25em}img{border:0}svg:not(:root){overflow:hidden}figure{margin:1em 40px}hr{-moz-box-sizing:content-box;box-sizing:content-box;height:0}pre{overflow:auto}code,kbd,pre,samp{font-family:monospace, monospace;font-size:1em}button,input,optgroup,select,textarea{color:inherit;font:inherit;margin:0}button{overflow:visible}button,select{text-transform:none}button,html input[type="button"],input[type="reset"],input[type="submit"]{-webkit-appearance:button;cursor:pointer}button[disabled],html input[disabled]{cursor:default}button::-moz-focus-inner,input::-moz-focus-inner{border:0;padding:0}input{line-height:normal}input[type="checkbox"],input[type="radio"]{box-sizing:border-box;padding:0}input[type="number"]::-webkit-inner-spin-button,input[type="number"]::-webkit-outer-spin-button{height:auto}input[type="search"]{-webkit-appearance:textfield;-moz-box-sizing:content-box;-webkit-box-sizing:content-box;box-sizing:content-box}input[type="search"]::-webkit-search-cancel-button,input[type="search"]::-webkit-search-decoration{-webkit-appearance:none}fieldset{border:1px solid #c0c0c0;margin:0 2px;padding:0.35em 0.625em 0.75em}legend{border:0;padding:0}textarea{overflow:auto}optgroup{font-weight:bold}table{border-collapse:collapse;border-spa